       p.sigla_pessoa                ,
       p.nome_pessoa                 ,
       cp.nm_orgao_usu               , --Nova coluna segundo chamado 2007/039151 para distinguir qual orgão a sigla pertence: Seção Judiciára ou Tribunal Reginal
       p.sexo_pessoa                 ,
       p.data_nasc_pessoa            ,
       p.tp_sanguineo_pessoa         ,
