       p.grau_instrucao_pessoa       ,
       p.email_pessoa                ,
       p.data_ini_pessoa             ,
       p.data_posse_pessoa           ,
       p.ato_nomeacao_pessoa         ,
       p.data_nomeacao_pessoa        ,
       p.data_publicacao_pessoa      ,
